import { type LoaderFunctionArgs } from '@remix-run/node';
export async function loader(_args: LoaderFunctionArgs){ const enc=new TextEncoder(); const stream=new ReadableStream({ start(c){ c.enqueue(enc.encode('data: hello\n\n')); } }); return new Response(stream,{ headers:{ 'Content-Type':'text/event-stream' } }); }
export default function Page(){ return null }
