import { json, type ActionFunctionArgs } from '@remix-run/node';
import { Form } from '@remix-run/react';
import { z } from 'zod';
export async function action({ request }: ActionFunctionArgs){ const s=z.object({ name:z.string() }); const f=await request.formData(); const r=s.safeParse({ name: f.get('name') }); if(!r.success) return json({ ok:false }, { status: 400 }); return json({ ok:true }); }
export default function(){ return <Form method="post"><input name="name"/><button>Send</button></Form>; }
