import { json, type LoaderFunctionArgs, type ActionFunctionArgs } from '@remix-run/node';
import { useLoaderData, Form } from '@remix-run/react';
import { z } from 'zod';
export async function loader({}: LoaderFunctionArgs){ return json({ hello: 'world' }); }
export async function action({ request }: ActionFunctionArgs){ const form = await request.formData(); const name = String(form.get('name')||''); const Body = z.object({ name: z.string().min(1) }); const r = Body.safeParse({ name }); return json({ ok: r.success }); }
export default function Index(){ const data = useLoaderData<typeof loader>(); return (<div><h1>{data.hello}</h1><Form method="post"><input name="name" /><button>send</button></Form></div>); }
