import { json, type ActionFunctionArgs } from '@remix-run/node';
import { z } from 'zod';
const schema = z.object({ email: z.string().email() });
export async function action({ request }: ActionFunctionArgs){ const fd = await request.formData(); const obj = Object.fromEntries(fd); const v = schema.safeParse(obj); if(!v.success) return json({ ok:false }, { status:422 }); return json({ ok:true }); }
export default function Page(){ return null }
