use actix_web::{get, App, HttpResponse, HttpServer, Responder};
#[get("/health")] async fn health() -> impl Responder { HttpResponse::Ok().json(serde_json::json!({"status":"ok"})) }
#[get("/")] async fn root() -> impl Responder { HttpResponse::Ok().json(serde_json::json!({"hello":"world"})) }
#[actix_web::main] async fn main() -> std::io::Result<()> { HttpServer::new(|| App::new().service(health).service(root)).bind(("127.0.0.1", 3000))?.run().await }
