const Fastify = require('fastify');
const { S3Client, CompleteMultipartUploadCommand } = require('@aws-sdk/client-s3');
const s3 = new S3Client({});
const app = Fastify();
app.post('/complete', async (req,res)=>{ const body = await req.body; const out = await s3.send(new CompleteMultipartUploadCommand({ Bucket: '{{bucket}}', Key: 'upload.bin', UploadId: body.uploadId, MultipartUpload: { Parts: body.parts } })); res.send(out); });
app.listen({ port: Number('{{port}}') });
