import { S3Client, SelectObjectContentCommand } from '@aws-sdk/client-s3';
const s3 = new S3Client({});
const out = await s3.send(new SelectObjectContentCommand({ Bucket: process.env.BUCKET || '{{bucket}}', Key: process.env.KEY || '{{key}}', ExpressionType: 'SQL', Expression: 'select * from s3object s limit 1', InputSerialization: { CSV: { FileHeaderInfo: 'NONE' } }, OutputSerialization: { JSON: {} } }));
console.log(!!out.Payload);
