const express = require('express');
const { isValidSignature, SIGNATURE_HEADER_NAME } = require('@sanity/webhook');
const app = express();
app.use(express.text({ type: '*/*' }));
app.post('/sanity', (req,res)=>{ const valid = isValidSignature(req.body, req.headers[SIGNATURE_HEADER_NAME], process.env.SANITY_WEBHOOK_SECRET); res.status(valid?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
