import { GraphQLClient, gql } from 'graphql-request';
const url = `https://${process.env.SHOPIFY_SHOP}/admin/api/2024-07/graphql.json`;
const client = new GraphQLClient(url, { headers: { 'X-Shopify-Access-Token': process.env.SHOPIFY_TOKEN } });
const m = gql`mutation($topic:WebhookSubscriptionTopic!,$callback:String!){ webhookSubscriptionCreate(topic:$topic, webhookSubscription:{callbackUrl:$callback, format:JSON}){ userErrors{field message} webhookSubscription{ id } } }`;
console.log('Call webhookSubscriptionCreate');
