import { GraphQLClient, gql } from 'graphql-request';
const url = `https://${process.env.SHOPIFY_SHOP}/api/2024-07/graphql.json`;
const client = new GraphQLClient(url, { headers: { 'X-Shopify-Storefront-Access-Token': process.env.SHOPIFY_STOREFRONT_TOKEN } });
const m = gql`mutation($lineItems:[CheckoutLineItemInput!]!){ checkoutCreate(input:{ lineItems:$lineItems }){ checkout{ id webUrl } userErrors{ field message } } }`;
console.log('Call checkoutCreate with sample lineItems');
