import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient(`https://${process.env.SHOPIFY_SHOP}/api/2024-07/graphql.json`, { headers: { 'X-Shopify-Storefront-Access-Token': process.env.SHOPIFY_STOREFRONT_TOKEN } });
const q = gql`{ shop { name } }`;
try { const r = await client.request(q); console.log(r.shop.name); } catch(e){ console.log('Provide valid Storefront token'); }
