const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.raw({ type: '*/*' }));
app.post('/slack', (req,res)=>{ const ts = req.headers['x-slack-request-timestamp']; const sig = req.headers['x-slack-signature']; const base = `v0:${ts}:${req.body}`; const my = 'v0=' + crypto.createHmac('sha256', process.env.SLACK_SIGNING_SECRET).update(base).digest('hex'); res.status(sig===my?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
