import Database from 'better-sqlite3';
const db = new Database('app.db');
db.exec('CREATE VIRTUAL TABLE IF NOT EXISTS docs USING fts5(content)');
db.exec("INSERT INTO docs(content) VALUES ('hello world')");
const rows = db.prepare(`SELECT rowid, content FROM docs WHERE docs MATCH 'hello'`).all();
console.log(rows.length);
