import { Client, Environment } from 'square';
const client = new Client({ accessToken: process.env.SQUARE_TOKEN, environment: Environment.Sandbox });
const r = await client.paymentsApi.createPayment({ sourceId: 'cnon:card-nonce-ok', idempotencyKey: String(Date.now()), amountMoney: { amount: 100, currency: 'USD' } });
console.log(r.result.payment?.id);
