files.template:
import { defineConfig } from 'astro/config';
import starlight from '@astrojs/starlight';

export default defineConfig({
  site: '{{site_url}}',
  integrations: [
    starlight({
      title: {
        default: '{{title_en}}',
        ja: '{{title_ja}}'
      },
      defaultLocale: 'en',
      locales: {
        en: { label: 'English' },
        ja: { label: '日本語' }
      },
      sidebar: [
        { label: 'Introduction', autogenerate: { directory: 'docs' } },
        { label: 'はじめに', autogenerate: { directory: 'docs/ja' } }
      ]
    }),
  ],
});
