import { defineConfig } from 'astro/config';
import starlight from '@astrojs/starlight';
export default defineConfig({
  site: '{{site_url}}',
  integrations: [ starlight({ title: '{{title}}', sidebar: [{ label: 'Introduction', autogenerate: { directory: 'docs' } }] }) ]
});

import fs from 'node:fs/promises';
import path from 'node:path';
import sharp from 'sharp';
const ROOT = process.cwd();
const DOCS = path.join(ROOT, 'src', 'content', 'docs');
const OUT = path.join(ROOT, 'public', 'og');
async function* walk(dir){ for (const ent of await fs.readdir(dir, { withFileTypes:true })) { const p = path.join(dir, ent.name); if (ent.isDirectory()) yield* walk(p); else if (/\.(md|mdx)$/i.test(ent.name)) yield p; } }
function parseFrontmatter(txt){ const m = txt.match(/^---
([\s\S]*?)
---/); if (!m) return {}; const res={}; for (const line of m[1].split(/
/)) { const k=line.split(':')[0]?.trim(); const v=line.slice((k||'').length+1).trim(); if(k) res[k]=v?.replace(/^['"']|['"']$/g,''); } return res; }
async function genOne(rel, title){ const svg = `<!DOCTYPE svg><svg width=\"1200\" height=\"630\" viewBox=\"0 0 1200 630\" xmlns=\"http://www.w3.org/2000/svg\"><defs><linearGradient id=\"g\" x1=\"0\" y1=\"0\" x2=\"1\" y2=\"1\"><stop offset=\"0\" stop-color=\"#0ea5e9\"/><stop offset=\"1\" stop-color=\"#1e293b\"/></linearGradient></defs><rect width=\"1200\" height=\"630\" fill=\"url(#g)\"/><text x=\"60\" y=\"360\" font-family=\"system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, Noto Sans, Helvetica Neue, Arial\" font-size=\"72\" fill=\"#fff\">${title}</text></svg>`; const outPath = path.join(OUT, rel.replace(/\.(md|mdx)$/i,'') + '.png'); await fs.mkdir(path.dirname(outPath), { recursive: true }); await sharp(Buffer.from(svg)).png().toFile(outPath); }
(async ()=>{ const tasks=[]; for await (const p of walk(DOCS)) { const rel = path.relative(DOCS, p); const txt = await fs.readFile(p,'utf8'); const fm = parseFrontmatter(txt); const title = fm.ogTitle || fm.title || rel; tasks.push(genOne(rel, title)); } await Promise.all(tasks); console.log('OG batch generated'); })();
