import { defineConfig } from 'astro/config';
import starlight from '@astrojs/starlight';
export default defineConfig({
  site: '{{site_url}}',
  integrations: [ starlight({ title: '{{title}}', sidebar: [{ label: 'Introduction', autogenerate: { directory: 'docs' } }] }) ]
});

import fs from 'node:fs/promises';
import sharp from 'sharp';

const title = process.env.OG_TITLE || '{{title}}';
const svg = `<!DOCTYPE svg PUBLIC \"-//W3C//DTD SVG 1.1//EN\" \"http://www.w3.org/Graphics/SVG/1.1/DTD/svg11.dtd\">
<svg width=\"1200\" height=\"630\" viewBox=\"0 0 1200 630\" xmlns=\"http://www.w3.org/2000/svg\">
  <defs>
    <linearGradient id=\"g\" x1=\"0\" y1=\"0\" x2=\"1\" y2=\"1\">
      <stop offset=\"0\" stop-color=\"#0ea5e9\"/>
      <stop offset=\"1\" stop-color=\"#1e293b\"/>
    </linearGradient>
  </defs>
  <rect width=\"1200\" height=\"630\" fill=\"url(#g)\"/>
  <text x=\"60\" y=\"360\" font-family=\"system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, Noto Sans, Helvetica Neue, Arial\" font-size=\"72\" fill=\"#fff\">${title}</text>
</svg>`;

const out = 'public/og.png';
await fs.mkdir('public', { recursive: true });
await sharp(Buffer.from(svg)).png().toFile(out);
console.log('OG generated:', out);
