{
  "name": "{{app_name}}",
  "private": true,
  "type": "module",
  "scripts": { "dev": "astro dev", "build": "astro build", "postbuild": "npx -y pagefind --site dist", "preview": "astro preview" },
  "devDependencies": { "astro": "latest", "@astrojs/starlight": "latest" },
  "dependencies": { "pagefind": "^1.0.0" }
}

import { defineConfig } from 'astro/config';
import starlight from '@astrojs/starlight';
export default defineConfig({
  site: '{{site_url}}',
  integrations: [ starlight({ title: '{{title}}', sidebar: [{ label: 'Introduction', autogenerate: { directory: 'docs' } }] }) ]
});
