import { SQSClient, ReceiveMessageCommand, DeleteMessageCommand } from '@aws-sdk/client-sqs';
const c = new SQSClient({}); const q='https://sqs.us-east-1.amazonaws.com/123/queue';
const r= await c.send(new ReceiveMessageCommand({ QueueUrl:q, WaitTimeSeconds:1 }));
for (const m of r.Messages||[]){ console.log(m.Body); await c.send(new DeleteMessageCommand({ QueueUrl:q, ReceiptHandle:m.ReceiptHandle! })); }
