import express from 'express';
import cookieParser from 'cookie-parser';
import csurf from 'csurf';
const app=express(); app.use(cookieParser()); app.use(csurf({ cookie:true }));
app.get('/form', (req,res)=> res.send(`<input type=hidden name=_csrf value="${req.csrfToken()}"/>`));
app.listen(3000);
