import express from 'express';
import helmet from 'helmet';
import cors from 'cors';
export function createApp(){ const app = express(); app.use(helmet()); app.use(cors()); return app; }

import { NodeSDK } from '@opentelemetry/sdk-node';
import { ConsoleSpanExporter } from '@opentelemetry/sdk-trace-base';
import { SimpleSpanProcessor } from '@opentelemetry/sdk-trace-base';
export function setupOtel(){ const sdk = new NodeSDK(); (sdk as any)._tracerProvider.addSpanProcessor(new SimpleSpanProcessor(new ConsoleSpanExporter())); sdk.start(); return sdk; }

import { setupOtel } from './tracer';
const sdk = setupOtel();
process.on('SIGTERM', ()=> sdk.shutdown().finally(()=> process.exit(0)));

