import { KinesisClient, PutRecordCommand } from '@aws-sdk/client-kinesis';
const client = new KinesisClient({});
export async function put(streamName: string, data: string, partitionKey='pk'){ await client.send(new PutRecordCommand({ StreamName: streamName, Data: new TextEncoder().encode(data), PartitionKey: partitionKey })); }

