import { ChatOpenAI } from '@langchain/openai';
import { ChatPromptTemplate } from '@langchain/core/prompts';
export async function run(prompt: string){
  const llm = new ChatOpenAI({ model: 'gpt-4o-mini', apiKey: process.env.OPENAI_API_KEY });
  const tpl = ChatPromptTemplate.fromMessages([['system','You are helpful.'],['human','{input}']]);
  const chain = tpl.pipe(llm);
  const res = await chain.invoke({ input: prompt });
  return res?.content?.toString?.() || String(res);
}
