import { MeiliSearch } from 'meilisearch';
export const meili = new MeiliSearch({ host: process.env.MEILI_HOST || 'http://127.0.0.1:7700', apiKey: process.env.MEILI_API_KEY });
export async function indexDoc(index: string, doc: any){ const i = meili.index(index); return i.addDocuments([doc]); }

