import { z } from 'zod';
import { ok, err } from '@/src/next/response';
const Payload = z.object({ name: z.string().min(1) });
export async function POST(req: Request){ try { const json = await req.json(); const parsed = Payload.parse(json); return ok({ message: 'Hello ' + parsed.name }); } catch(e){ return err(e, 400); } }

import type { NextRequest } from 'next/server';
export function withRole(required: 'user'|'admin', handler: (req: NextRequest)=>Promise<Response>|Response){
  return async (req: NextRequest) => {
    const role = (req.headers.get('x-role') as 'user'|'admin') || 'user';
    if (required === 'admin' && role !== 'admin') {
      return new Response('Forbidden', { status: 403 });
    }
    return handler(req);
  };
}

export const securityHeaders = [
  { key: 'X-Frame-Options', value: 'DENY' },
  { key: 'X-Content-Type-Options', value: 'nosniff' },
  { key: 'Referrer-Policy', value: 'strict-origin-when-cross-origin' }
];

