import { NextResponse } from 'next/server';
import { z } from 'zod';
const Body = z.object({ message: z.string().min(1) });
export async function POST(req: Request){ const json = await req.json(); const parsed = Body.safeParse(json); if(!parsed.success) return NextResponse.json({ error: parsed.error.format() }, { status: 400 }); return NextResponse.json({ echo: parsed.data.message }); }
