import { z } from 'zod';
const Body = z.object({ message: z.string().min(1) });
export async function POST(req: Request){ const json = await req.json(); const p=Body.safeParse(json); if(!p.success) return Response.json({ error:p.error.format() }, { status:400 }); return Response.json({ ok:true, echo:p.data.message }); }
