'use server';
import { z } from 'zod';
const Form = z.object({ name: z.string().min(1) });
export async function submit(formData: FormData){ const parsed = Form.safeParse({ name: String(formData.get('name')||'') }); if(!parsed.success) return { ok:false, error: parsed.error.flatten() }; return { ok:true, data: parsed.data }; }
