import { NextResponse } from 'next/server';
export async function POST(req: Request){
  const { priceId } = await req.json();
  // 実装時に Stripe の Checkout Session を作成してください。
  return NextResponse.json({ url: 'https://checkout.stripe.com/pay/mock' , priceId });
}

export const messages = {
  en: { purchase: 'Purchase', success: 'Success' },
  ja: { purchase: '購入', success: '成功' }
};
