import express from 'express';
import { z } from 'zod';
const app = express();
app.use(express.json());
const Body = z.object({ name: z.string() });
app.post('/users', (req, res)=>{ const parsed = Body.safeParse(req.body); if(!parsed.success) return res.status(400).json(parsed.error.format()); res.json({ id: 1, name: parsed.data.name }); });
app.listen(3000);
