from fastapi import FastAPI, HTTPException, Header
app = FastAPI()
@app.get('/health')
async def health():
    return { 'ok': True }
@app.get('/secure')
async def secure(x_token: str | None = Header(default=None)):
    if x_token != 'secret':
        raise HTTPException(status_code=401, detail='unauthorized')
    return { 'ok': True }
