import { createClient } from 'redis';
const url = process.env.REDIS_URL || 'redis://localhost:6379';
export const redis = createClient({ url });
redis.on('error', (e)=> console.error('Redis error', e));
export async function connect(){ if(!redis.isOpen) await redis.connect(); }
export async function get(key: string){ await connect(); return redis.get(key); }
export async function set(key: string, val: string, ttl?: number){ await connect(); if (ttl) return redis.set(key, val, { EX: ttl }); return redis.set(key, val); }
