import { configureStore } from '@reduxjs/toolkit';
import counter from './counterSlice';
export const store = configureStore({ reducer: { counter } });
export type RootState = ReturnType<typeof store.getState>;
export type AppDispatch = typeof store.dispatch;

import { createSlice, PayloadAction } from '@reduxjs/toolkit';
interface State { value: number }
const initialState: State = { value: 0 };
const slice = createSlice({ name: 'counter', initialState, reducers: { inc: (s)=> { s.value += 1; }, add: (s, a: PayloadAction<number>)=> { s.value += a.payload; } } });
export const { inc, add } = slice.actions;
export default slice.reducer;

