from fastapi import APIRouter, Request
router = APIRouter()
@router.get('/api/tree')
async def get_tree():
    return [{ 'id': 'root', 'label': 'Root' }]
@router.post('/api/tree')
async def post_tree(request: Request):
    data = await request.json()
    return { 'ok': True, 'received': data }
