export function toFlow(tree: any[]){
  const nodes: any[] = []; const edges: any[] = [];
  const walk = (n: any, parent?: any, idx=0)=>{ nodes.push({ id: n.id, data: { label: n.label }, position: { x: idx*160, y: (parent? 100:0) + (idx*40) } }); if(parent){ edges.push({ id: parent.id+'-'+n.id, source: parent.id, target: n.id }); } (n.children||[]).forEach((c: any, i: number)=> walk(c, n, i)); };
  (tree||[]).forEach((n, i)=> walk(n, undefined, i));
  return { nodes, edges };
}

// Convert ReactFlow graph back to a flat tree (simple heuristic)
export function fromFlow(nodes: any[], edges: any[]){
  const byId = new Map(nodes.map(n=> [n.id, { id: n.id, label: n.data?.label || n.id, children: [] as any[] }]));
  const hasParent = new Set<string>();
  for (const e of edges||[]){ const p = byId.get(e.source); const c = byId.get(e.target); if (p && c){ (p.children as any[]).push(c); hasParent.add(e.target); } }
  const roots = nodes.filter(n=> !hasParent.has(n.id)).map(n=> byId.get(n.id));
  return roots;
}

export async function saveTreeGraphQL(endpoint: string, tree: any){
  const mutation = `mutation($tree: JSON!){ saveTree(tree:$tree) }`;
  const res = await fetch(endpoint, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ query: mutation, variables: { tree } }) });
  if (!res.ok) throw new Error('graphql_save_failed:' + res.status);
  return res.json();
}
export async function loadTreeGraphQL(endpoint: string){
  const query = `query{ tree }`;
  const res = await fetch(endpoint, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ query }) });
  if (!res.ok) throw new Error('graphql_load_failed:' + res.status);
  return res.json();
}

