import Stripe from 'stripe';
const apiKey = process.env.STRIPE_API_KEY || '';
export const stripe = new Stripe(apiKey, { apiVersion: '2024-06-20' as any });
export async function createCheckoutSession(priceId: string){
  return stripe.checkout.sessions.create({ mode: 'subscription', line_items: [{ price: priceId, quantity: 1 }], success_url: 'https://example.com/success', cancel_url: 'https://example.com/cancel' });
}

