files.template:
terraform { required_version = ">= 1.5.0" required_providers { aws = { source = "hashicorp/aws" version = ">= 5.0.0" } } }
provider "aws" { region = var.region }
variable "region" { type = string default = "us-east-1" }
resource "aws_s3_bucket" "site" { bucket = "example-static-site-123" website { index_document = "index.html" } }
