import Stripe from 'stripe';
const stripe = new Stripe(process.env.STRIPE_API_KEY);
const session = await stripe.checkout.sessions.create({ mode: 'payment', line_items: [{ price: process.env.PRICE_ID || '{{priceId}}', quantity: 1 }], success_url: 'https://example.com/s', cancel_url: 'https://example.com/c' });
console.log(session.id);
