const express = require('express');
const stripe = require('stripe')();
const app = express();
app.post('/checkout', async (_req, res) => {
  const session = await stripe.checkout.sessions.create({ mode: 'payment', line_items: [{ price: '{{price_id}}', quantity: 1 }], success_url: 'https://example.com/success', cancel_url: 'https://example.com/cancel' });
  res.json({ url: session.url });
});
app.get('/health', (_req, res) => res.json({ status: 'ok' }));
app.listen(Number('{{port}}'), () => console.log('listening on {{port}}'));
