import Stripe from 'stripe';
const stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
const acct = await stripe.accounts.create({ type: 'express' });
const link = await stripe.accountLinks.create({ account: acct.id, refresh_url: 'https://example.com/refresh', return_url: 'https://example.com/return', type: 'account_onboarding' });
console.log('Onboarding URL:', link.url);
