import useSWR, { SWRConfig } from 'swr';
const fetcher = (u:string)=> fetch(u).then(r=>r.json());
function Inner(){ const { data } = useSWR('/api', fetcher); return <div>{data? 'ok':'...'}</div>; }
export default function App(){ return <SWRConfig value={{ fetcher, revalidateOnFocus:false }}><Inner/></SWRConfig>; }
