import useSWRInfinite from 'swr/infinite';
const fetcher = (url: string)=> fetch(url).then(r=>r.json());
export default function App(){ const getKey = (i:number)=> `https://example.com/api/items?page=${i+1}`; const { data, size, setSize } = useSWRInfinite(getKey, fetcher); const items = (data||[]).flat(); return (<div>{items.map((x:any,i:number)=>(<div key={i}>{x.id}</div>))}<button onClick={()=> setSize(size+1)}>もっと読む</button></div>); }
