import { useQuery, QueryClient, QueryClientProvider } from '@tanstack/react-query';
const qc = new QueryClient();
function Inner(){ const q = useQuery({ queryKey: ['hello'], queryFn: async()=>({ ok:true }) }); return <div>{String(q.data?.ok)}</div>; }
export default function App(){ return <QueryClientProvider client={qc}><Inner/></QueryClientProvider>; }
