import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { persistQueryClient } from '@tanstack/react-query-persist-client';
import { createSyncStoragePersister } from '@tanstack/query-sync-storage-persister';
const qc = new QueryClient();
if (typeof window !== 'undefined'){ persistQueryClient({ queryClient: qc, persister: createSyncStoragePersister({ storage: window.localStorage }) }); }
export default function App(){ return <QueryClientProvider client={qc}><div>OK</div></QueryClientProvider>; }
