import * as React from 'react';
import { useReactTable, getCoreRowModel, flexRender, createColumnHelper } from '@tanstack/react-table';
const ch = createColumnHelper<{ id:number; name:string }>();
const columns = [ ch.accessor('id',{ header:'ID' }), ch.accessor('name',{ header:'Name' }) ];
const data = [{id:1,name:'A'},{id:2,name:'B'}];
export function Table(){ const table = useReactTable({ data, columns, getCoreRowModel: getCoreRowModel() });
  return (<table><thead>{table.getHeaderGroups().map(hg=>(<tr key={hg.id}>{hg.headers.map(h=>(<th key={h.id}>{flexRender(h.column.columnDef.header, h.getContext())}</th>))}</tr>))}</thead>
  <tbody>{table.getRowModel().rows.map(r=>(<tr key={r.id}>{r.getVisibleCells().map(c=>(<td key={c.id}>{flexRender(c.column.columnDef.cell, c.getContext())}</td>))}</tr>))}</tbody></table>); }
