import * as React from 'react';
import { useReactTable, getCoreRowModel, flexRender, createColumnHelper } from '@tanstack/react-table';
const data = [{id:1,name:'A'},{id:2,name:'B'}];
const ch = createColumnHelper<{ id:number; name:string }>();
const base = [ ch.accessor('id',{ header:'ID' }), ch.accessor('name',{ header:'Name' }) ];
export function TableRowSelection(){ const [rowSelection,setRowSelection]=React.useState({}); const table = useReactTable({ data, columns: base, state:{rowSelection}, onRowSelectionChange:setRowSelection, getCoreRowModel: getCoreRowModel(), enableRowSelection:true });
  return (<table><thead>{table.getHeaderGroups().map(hg=>(<tr key={hg.id}>{hg.headers.map(h=>(<th key={h.id}>{flexRender(h.column.columnDef.header, h.getContext())}</th>))}</tr>))}</thead><tbody>{table.getRowModel().rows.map(r=>(<tr key={r.id} onClick={()=>r.toggleSelected()} style={{background:r.getIsSelected()?'#eef':''}}>{r.getVisibleCells().map(c=>(<td key={c.id}>{flexRender(c.column.columnDef.cell, c.getContext())}</td>))}</tr>))}</tbody></table>); }
