files.template:
provider "aws" { region = var.region }
resource "aws_s3_bucket" "origin" { bucket = "demo-origin-bucket" }
resource "aws_cloudfront_distribution" "cdn" { enabled = true origins { domain_name = aws_s3_bucket.origin.bucket_regional_domain_name origin_id = "s3origin" } default_cache_behavior { viewer_protocol_policy = "redirect-to-https" target_origin_id = "s3origin" allowed_methods = ["GET","HEAD"] cached_methods = ["GET","HEAD"] forwarded_values { query_string = false cookies { forward = "none" } } } viewer_certificate { cloudfront_default_certificate = true } }
variable "region" { default = "us-east-1" }
