files.template:
provider "aws" { region = var.region }
resource "aws_security_group" "redis" { name = "redis-sg" }
resource "aws_elasticache_subnet_group" "redis" { name = "redis-subnets" subnet_ids = [] }
resource "aws_elasticache_cluster" "redis" { cluster_id = "redis-demo" engine = "redis" node_type = "cache.t2.micro" num_cache_nodes = 1 subnet_group_name = aws_elasticache_subnet_group.redis.name security_group_ids = [aws_security_group.redis.id] }
variable "region" { default = "us-east-1" }
