files.template:
resource "aws_lambda_function" "fn" { function_name = "fn" runtime = "nodejs18.x" role = aws_iam_role.r.arn handler = "index.handler" filename = "fn.zip" }
resource "aws_iam_role" "r" { name = "r" assume_role_policy = data.aws_iam_policy_document.assume.json }
data "aws_iam_policy_document" "assume" { statement { actions=["sts:AssumeRole"], principals { type="Service" identifiers=["lambda.amazonaws.com"] } } }
