files.template:
provider "aws" { region = var.region }
resource "aws_s3_bucket" "site" { bucket = var.bucket force_destroy = true }
resource "aws_cloudfront_distribution" "cdn" { enabled = true origins { domain_name = aws_s3_bucket.site.bucket_regional_domain_name origin_id = "s3" } default_cache_behavior { viewer_protocol_policy = "redirect-to-https" target_origin_id = "s3" allowed_methods = ["GET","HEAD"] cached_methods = ["GET","HEAD"] forwarded_values { query_string = false cookies { forward = "none" } } } viewer_certificate { cloudfront_default_certificate = true } }
variable "region" { default = "us-east-1" }
variable "bucket" { default = "my-static-site-demo" }
